sys_script_include.script:
var SourceUtility = Class.create();
SourceUtility.prototype = {
    initialize: function() {

    },
    GetAllItems: function() {
        var body = [];
        // 1. Create an object to store rows from a table	
        var incidentGR = new GlideRecord('x_58872_needit_source_import_incident');

        // 3. Execute query 
        incidentGR.query();

        // 4. Process returned records
        while (incidentGR.next()) {

			var requested_for=incidentGR.getDisplayValue("requested_for");
			var short_description=incidentGR.getDisplayValue("short_description");

            body.push({
                "u_requested_for": requested_for,
                "u_short_description": short_description
            });
        }
        return body;
    },
    type: 'SourceUtility'
};
