sys_script_include.script:
var SourceUtility = Class.create();
SourceUtility.prototype = {
    initialize: function() {

    },
    getAllItems: function() {
        var body = [];
        // 1. Create an object to store rows from a table	
        var gr_incident = new GlideRecord('x_58872_needit_source_import_incident');

        // 3. Execute query 
        gr_incident.query();

        // 4. Process returned records
        while (gr_incident.next()) {

            var requested_for = gr_incident.getDisplayValue("requested_for");
            var short_description = gr_incident.getDisplayValue("short_description");

            body.push({
                "u_requested_for": requested_for,
                "u_short_description": short_description
            });
        }
        return body;
    },
    sendAllItemForMassImport: function(current, previous) {

        var body = getAllItems();

        var req_insertItems = new sn_ws.RESTMessageV2();
        req_insertItems.setEndpoint('https://dev100966.service-now.com/api/x_58872_needit/receive_all_record_and_call_import');

        req_insertItems.setHttpMethod('post');

        req_insertItems.setRequestHeader("Accept", "application/json");
        req_insertItems.setRequestBody(JSON.stringify(body));
        gs.info("body to send :" + body.length);

        var user = 'admin';
        var password = '21YVJnPTqhoj';
        req_insertItems.setBasicAuth(user, password);
        var response = req_insertItems.execute();

        var httpStatus = response.getStatusCode();
        gs.info("HttpStatus :" + httpStatus);
    },
    sendImportItem: function(current, previous) {
        try {

            var req = new sn_ws.RESTMessageV2('x_58872_needit.Call Import Web service', 'Call Web Service');

            var short_description = current.getDisplayValue("short_description");
            var requested_for = current.getDisplayValue("short_description");

            gs.info("short_description:" + short_description);
            gs.info("requested_for:" + requested_for);

            req.setStringParameterNoEscape('short_description', short_description);
            req.setStringParameterNoEscape('requested_for', requested_for);


            var body = {};
            body.u_requested_for = requested_for;
            body.u_short_description = short_description;


            req.setRequestHeader("Accept", "application/json");
            req.setRequestBody(JSON.stringify(body));


            var user = 'admin';
            var password = '21YVJnPTqhoj';
            req.setBasicAuth(user, password);
            var response = req.execute();
            //var responseBody = response.getBody();
            var httpStatus = response.getStatusCode();
            if (httpStatus != '201')
                current.setAbortAction(true);

        } catch (ex) {
            var message = ex.message;
        }
    },
    type: 'SourceUtility'
};
